export default function SocialLinks({ className }: { className?: string }) {
  return (
    <ul className={`${className}`}>
      {links.map((link) => (
        <li key={link.href}>
          <a
            href={link.href}
            target='_blank'
            rel='noopener noreferrer'
            aria-label={link.label}
          >
            {link.icon()}
          </a>
        </li>
      ))}
    </ul>
  );
}
